  def nav_icon(assigns) do
    case assigns.type do
      :house ->
        icon_house(assigns)

      :system ->
        icon_system(assigns)

      :app ->
        icon_app(assigns)

      :cpu ->
        icon_cpu(assigns)
    end
  end

